import React, { useState, useEffect } from 'react';
import { FiChevronLeft, FiChevronRight, FiHeart, FiX } from 'react-icons/fi';
import axios from 'axios';

const HomePage = () => {
  const [images, setImages] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [favorites, setFavorites] = useState([]);
  const [showLightbox, setShowLightbox] = useState(false);
  const [loading, setLoading] = useState(true);

  // Load favorites from localStorage on initial render
  useEffect(() => {
    const savedFavorites = localStorage.getItem('favorites');
    if (savedFavorites) {
      setFavorites(JSON.parse(savedFavorites));
    }
  }, []);

  // Save favorites to localStorage whenever favorites change
  useEffect(() => {
    localStorage.setItem('favorites', JSON.stringify(favorites));
  }, [favorites]);

  useEffect(() => {
    fetchImages();
  }, []);

  const fetchImages = async () => {
    try {
      const response = await axios.get('/images');
      setImages(response.data);
      setLoading(false);
    } catch (err) {
      console.error('Failed to load images:', err);
      setLoading(false);
    }
  };

  // Auto-advance slideshow
  useEffect(() => {
    if (images.length === 0) return;

    const interval = setInterval(() => {
      setCurrentIndex(prevIndex => (prevIndex + 1) % images.length);
    }, 5000); // Change image every 5 seconds

    return () => clearInterval(interval);
  }, [images.length]);

  const goToPrevious = () => {
    setCurrentIndex(prevIndex => 
      prevIndex === 0 ? images.length - 1 : prevIndex - 1
    );
  };

  const goToNext = () => {
    setCurrentIndex(prevIndex => 
      prevIndex === images.length - 1 ? 0 : prevIndex + 1
    );
  };

  const goToSlide = (index) => {
    setCurrentIndex(index);
  };

  // Toggle favorite status for an image
  const toggleFavorite = (imageId) => {
    if (favorites.includes(imageId)) {
      setFavorites(favorites.filter(id => id !== imageId));
    } else {
      setFavorites([...favorites, imageId]);
    }
  };

  // Keyboard navigation
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (e.key === 'ArrowLeft') {
        goToPrevious();
      } else if (e.key === 'ArrowRight') {
        goToNext();
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, []);

  if (loading || images.length === 0) {
    return (
      <div className="slideshow-container" style={{ backgroundColor: '#FFF5E1' }}>
        <div className="flex flex-col items-center justify-center h-full">
          <div className="w-16 h-16 border-4 border-[#FF6F61] border-t-transparent rounded-full animate-spin mb-4"></div>
          <div className="text-cream-900 text-xl">Loading slideshow...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="slideshow-container">
      {/* Slideshow slides */}
      {images.map((image, index) => (
        <div 
          key={image.id}
          className={`slideshow-slide ${index === currentIndex ? 'active' : ''}`}
        >
          <img
            src={`http://localhost:5000${image.path}`}
            alt={image.original_name}
            className="w-full h-full object-cover"
            onClick={() => setShowLightbox(true)}
          />

          {/* Favorite button on slideshow */}
          <button
            className={`absolute top-4 right-4 p-2 rounded-full transition-colors ${
              favorites.includes(image.id) 
                ? 'bg-[#FF6F61] text-white' 
                : 'bg-white/70 text-[#001F3F]'
            }`}
            onClick={(e) => {
              e.stopPropagation();
              toggleFavorite(image.id);
            }}
          >
            <FiHeart 
              className={favorites.includes(image.id) ? 'fill-current' : ''} 
              size={20} 
            />
          </button>
        </div>
      ))}

      {/* Slideshow controls */}
      <div className="slideshow-controls">
        <button 
          className="slideshow-control-btn"
          onClick={goToPrevious}
        >
          <FiChevronLeft />
        </button>
        <button 
          className="slideshow-control-btn"
          onClick={goToNext}
        >
          <FiChevronRight />
        </button>
      </div>

      {/* Slideshow navigation dots */}
      <div className="slideshow-nav">
        {images.map((_, index) => (
          <div
            key={index}
            className={`slideshow-dot ${index === currentIndex ? 'active' : ''}`}
            onClick={() => goToSlide(index)}
          ></div>
        ))}
      </div>

      {/* Lightbox for slideshow image */}
      {showLightbox && images.length > 0 && (
        <div 
          className="lightbox-overlay"
          onClick={() => setShowLightbox(false)}
        >
          <div className="lightbox-content">
            <img
              src={`http://localhost:5000${images[currentIndex].path}`}
              alt={images[currentIndex].original_name}
              className="lightbox-image border border-[#708090]"
              onClick={(e) => e.stopPropagation()}
            />

            <button
              className="close-button"
              onClick={() => setShowLightbox(false)}
            >
              <FiX />
            </button>

            <div className="lightbox-nav">
              <button className="nav-button" onClick={(e) => { e.stopPropagation(); goToPrevious(); }}>
                <FiChevronLeft />
              </button>
              <button className="nav-button" onClick={(e) => { e.stopPropagation(); goToNext(); }}>
                <FiChevronRight />
              </button>
            </div>

            <div className="absolute top-4 left-0 right-0 text-center text-sm text-[#001F3F]">
              {currentIndex + 1} of {images.length}
              <button 
                className={`ml-4 text-xs ${favorites.includes(images[currentIndex].id) ? 'text-[#FF6F61]' : 'text-[#001F3F]'}`}
                onClick={(e) => {
                  e.stopPropagation();
                  toggleFavorite(images[currentIndex].id);
                }}
              >
                <FiHeart 
                  className={`inline mr-1 ${favorites.includes(images[currentIndex].id) ? 'fill-current text-[#FF6F61]' : ''}`} 
                  size={14} 
                />
                {favorites.includes(images[currentIndex].id) ? 'Favorited' : 'Favorite'}
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default HomePage;
