import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FiX, FiChevronLeft, FiChevronRight, FiTrash2 } from 'react-icons/fi';

const GalleryPage = () => {
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [selectedImageIndex, setSelectedImageIndex] = useState(null);
  const [deleting, setDeleting] = useState(null);

  useEffect(() => {
    fetchImages();
  }, []);

  const fetchImages = async () => {
    try {
      const response = await axios.get('/images');
      setImages(response.data);
      setLoading(false);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to load images');
      setLoading(false);
    }
  };

  const openLightbox = (index) => {
    setSelectedImageIndex(index);
  };

  const closeLightbox = () => {
    setSelectedImageIndex(null);
  };

  const goToPrevious = () => {
    if (selectedImageIndex === 0) {
      setSelectedImageIndex(images.length - 1);
    } else {
      setSelectedImageIndex(selectedImageIndex - 1);
    }
  };

  const goToNext = () => {
    if (selectedImageIndex === images.length - 1) {
      setSelectedImageIndex(0);
    } else {
      setSelectedImageIndex(selectedImageIndex + 1);
    }
  };

  const deleteImage = async (id, index) => {
    if (!window.confirm('Are you sure you want to delete this image?')) {
      return;
    }

    setDeleting(id);

    try {
      await axios.delete(`/images/${id}`);

      // Update the state to remove the image
      setImages(prevImages => prevImages.filter((_, i) => i !== index));

      // Adjust the selected index if needed
      if (selectedImageIndex !== null) {
        if (index < selectedImageIndex) {
          setSelectedImageIndex(selectedImageIndex - 1);
        } else if (index === selectedImageIndex) {
          setSelectedImageIndex(null);
        }
      }
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to delete image');
    } finally {
      setDeleting(null);
    }
  };

  // Keyboard navigation
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (selectedImageIndex === null) return;

      if (e.key === 'Escape') {
        closeLightbox();
      } else if (e.key === 'ArrowLeft') {
        goToPrevious();
      } else if (e.key === 'ArrowRight') {
        goToNext();
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [selectedImageIndex, images.length]);

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-dark-900">
        <div className="text-white text-xl">Loading gallery...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-dark-900">
        <div className="text-red-500 text-xl">{error}</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-dark-900 p-4 md:p-8">
      <div className="max-w-7xl mx-auto">
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-3xl font-bold text-white">Photo Gallery</h1>
          <div className="text-dark-300">
            {images.length} {images.length === 1 ? 'photo' : 'photos'}
          </div>
        </div>

        {images.length === 0 ? (
          <div className="flex flex-col items-center justify-center py-20 text-center">
            <div className="text-5xl mb-4">ðŸ“¸</div>
            <h2 className="text-2xl font-bold text-white mb-2">No Photos Yet</h2>
            <p className="text-dark-300 mb-6">Upload some beautiful photos to get started</p>
            <a
              href="/upload"
              className="bg-primary-600 hover:bg-primary-700 text-white px-6 py-3 rounded-lg font-medium"
            >
              Upload Photos
            </a>
          </div>
        ) : (
          <div className="masonry-grid">
            {images.map((image, index) => (
              <div key={image._id} className="relative group cursor-pointer" 
                style={{ aspectRatio: image.width / image.height }}
                onClick={() => openLightbox(index)}>
                <img
                  src={`http://localhost:5000/${image.thumbnailPath}`}
                  srcSet={`http://localhost:5000/${image.smallPath} 600w, http://localhost:5000/${image.mediumPath} 1200w`}
                  sizes="(max-width: 640px) 100vw, (max-width: 768px) 50vw, (max-width: 1024px) 33vw, 25vw"
                  alt={image.originalName}
                  className="w-full h-full object-cover rounded-lg shadow-lg transition-transform duration-300 group-hover:scale-105"
                  loading="lazy"
                />

                {/* Delete button */}
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    deleteImage(image._id, index);
                  }}
                  disabled={deleting === image._id}
                  className={`absolute top-2 right-2 bg-red-500 hover:bg-red-600 text-white rounded-full p-2 opacity-0 group-hover:opacity-100 transition-opacity ${
                    deleting === image._id ? 'opacity-100' : ''
                  }`}
                >
                  {deleting === image._id ? (
                    <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
                  ) : (
                    <FiTrash2 size={16} />
                  )}
                </button>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Lightbox */}
      {selectedImageIndex !== null && images.length > 0 && (
        <div 
          className="lightbox-overlay"
          onClick={closeLightbox}
        >
          <div className="lightbox-content">
            <img
              src={`http://localhost:5000/${images[selectedImageIndex].mediumPath}`}
              alt={images[selectedImageIndex].originalName}
              className="lightbox-image"
              onClick={(e) => e.stopPropagation()}
            />

            <button
              className="close-button"
              onClick={closeLightbox}
            >
              <FiX />
            </button>

            <div className="lightbox-nav">
              <button className="nav-button" onClick={(e) => { e.stopPropagation(); goToPrevious(); }}>
                <FiChevronLeft />
              </button>
              <button className="nav-button" onClick={(e) => { e.stopPropagation(); goToNext(); }}>
                <FiChevronRight />
              </button>
            </div>

            <div className="absolute bottom-4 left-0 right-0 text-center text-white text-sm">
              {selectedImageIndex + 1} of {images.length}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default GalleryPage;
