import React, { useCallback, useState } from 'react';
import { useDropzone } from 'react-dropzone';
import axios from 'axios';
import { FiUpload, FiX, FiImage, FiCheck } from 'react-icons/fi';

const UploadPage = () => {
  const [files, setFiles] = useState([]);
  const [uploading, setUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [uploadSuccess, setUploadSuccess] = useState(false);
  const [error, setError] = useState('');

  const onDrop = useCallback((acceptedFiles) => {
    // Filter out non-image files
    const imageFiles = acceptedFiles.filter(file => file.type.startsWith('image/'));

    // Validate file size (max 10MB)
    const validFiles = imageFiles.filter(file => file.size <= 10 * 1024 * 1024);

    // Show error for files that are too large
    const invalidFiles = imageFiles.filter(file => file.size > 10 * 1024 * 1024);
    if (invalidFiles.length > 0) {
      setError(`${invalidFiles.length} file(s) are too large (max 10MB)`);
      setTimeout(() => setError(''), 3000);
    }

    // Add valid files with preview URLs
    const newFiles = validFiles.map(file => {
      file.preview = URL.createObjectURL(file);
      return file;
    });

    setFiles(prev => [...prev, ...newFiles]);
  }, []);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'image/*': ['.jpeg', '.jpg', '.png', '.gif', '.webp']
    },
    maxFiles: 10,
    multiple: true
  });

  const removeFile = (index) => {
    const newFiles = [...files];
    // Revoke the preview URL to free up memory
    URL.revokeObjectURL(newFiles[index].preview);
    newFiles.splice(index, 1);
    setFiles(newFiles);
  };

  const uploadFiles = async () => {
    if (files.length === 0) {
      setError('Please select at least one image to upload');
      return;
    }

    setUploading(true);
    setUploadProgress(0);
    setError('');

    const formData = new FormData();
    files.forEach(file => {
      formData.append('images', file);
    });

    try {
      const response = await axios.post('/images/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        },
        onUploadProgress: (progressEvent) => {
          const progress = Math.round(
            (progressEvent.loaded * 100) / progressEvent.total
          );
          setUploadProgress(progress);
        }
      });

      if (response.status === 201) {
        setUploadSuccess(true);
        setFiles([]);
        setTimeout(() => {
          setUploadSuccess(false);
        }, 3000);
      }
    } catch (err) {
      setError(err.response?.data?.message || 'Upload failed');
    } finally {
      setUploading(false);
    }
  };

  const clearAll = () => {
    // Revoke all preview URLs
    files.forEach(file => URL.revokeObjectURL(file.preview));
    setFiles([]);
  };

  return (
    <div className="min-h-screen bg-dark-900 p-4 md:p-8">
      <div className="max-w-6xl mx-auto">
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-3xl font-bold text-white">Upload Photos</h1>
          {files.length > 0 && (
            <button
              onClick={clearAll}
              className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded-lg flex items-center"
            >
              <FiX className="mr-2" /> Clear All
            </button>
          )}
        </div>

        {error && (
          <div className="bg-red-500 text-white p-3 rounded-lg mb-4">
            {error}
          </div>
        )}

        {uploadSuccess && (
          <div className="bg-green-500 text-white p-3 rounded-lg mb-4 flex items-center">
            <FiCheck className="mr-2" /> Upload successful!
          </div>
        )}

        {/* Dropzone Area */}
        <div
          {...getRootProps()}
          className={`border-2 border-dashed rounded-xl p-8 text-center cursor-pointer transition-colors ${
            isDragActive
              ? 'border-primary-500 bg-primary-900/20'
              : 'border-dark-600 hover:border-dark-500 bg-dark-800'
          }`}
        >
          <input {...getInputProps()} />
          <div className="flex flex-col items-center justify-center">
            <FiUpload className="text-4xl text-dark-400 mb-3" />
            <p className="text-dark-300 mb-1">
              {isDragActive ? 'Drop the images here!' : 'Drag & drop images here'}
            </p>
            <p className="text-dark-500 text-sm">or click to browse</p>
            <p className="text-dark-500 text-sm mt-2">
              Supports: JPEG, PNG, GIF, WEBP (Max 10MB each)
            </p>
          </div>
        </div>

        {/* Upload Progress */}
        {uploading && (
          <div className="mt-6 bg-dark-800 rounded-lg p-4">
            <div className="flex justify-between items-center mb-2">
              <span className="text-white">Uploading...</span>
              <span className="text-dark-300">{uploadProgress}%</span>
            </div>
            <div className="w-full bg-dark-700 rounded-full h-2.5">
              <div
                className="bg-primary-600 h-2.5 rounded-full transition-all duration-300"
                style={{ width: `${uploadProgress}%` }}
              ></div>
            </div>
          </div>
        )}

        {/* Selected Files Preview */}
        {files.length > 0 && (
          <div className="mt-8">
            <h2 className="text-xl font-semibold text-white mb-4">
              Selected Images ({files.length})
            </h2>
            <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-6 gap-4">
              {files.map((file, index) => (
                <div key={index} className="relative group">
                  <div className="aspect-square bg-dark-800 rounded-lg overflow-hidden">
                    <img
                      src={file.preview}
                      alt={file.name}
                      className="w-full h-full object-cover"
                    />
                  </div>
                  <div className="absolute inset-0 bg-black/50 opacity-0 group-hover:opacity-100 transition-opacity flex items-center justify-center">
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        removeFile(index);
                      }}
                      className="bg-red-500 hover:bg-red-600 text-white rounded-full p-2"
                    >
                      <FiX />
                    </button>
                  </div>
                  <div className="absolute bottom-0 left-0 right-0 bg-black/70 p-2 text-white text-xs truncate">
                    {file.name}
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Upload Button */}
        <div className="mt-8 flex justify-center">
          <button
            onClick={uploadFiles}
            disabled={uploading || files.length === 0}
            className={`px-6 py-3 rounded-lg font-semibold flex items-center ${
              uploading || files.length === 0
                ? 'bg-gray-600 cursor-not-allowed'
                : 'bg-primary-600 hover:bg-primary-700'
            }`}
          >
            <FiUpload className="mr-2" />
            {uploading ? 'Uploading...' : `Upload ${files.length} Image(s)`}
          </button>
        </div>
      </div>
    </div>
  );
};

export default UploadPage;
