import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FiX, FiChevronLeft, FiChevronRight } from 'react-icons/fi';

const GalleryPage = () => {
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [selectedImageIndex, setSelectedImageIndex] = useState(null);
  const [currentSlide, setCurrentSlide] = useState(0);
  const [isSlideshow, setIsSlideshow] = useState(false);

  useEffect(() => {
    fetchImages();
  }, []);

  const fetchImages = async () => {
    try {
      const response = await axios.get('/images');
      setImages(response.data);
      setLoading(false);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to load images');
      setLoading(false);
    }
  };

  const openLightbox = (index) => {
    setSelectedImageIndex(index);
    setCurrentSlide(index);
  };

  const closeLightbox = () => {
    setSelectedImageIndex(null);
  };

  const goToPrevious = () => {
    if (currentSlide === 0) {
      setCurrentSlide(images.length - 1);
    } else {
      setCurrentSlide(currentSlide - 1);
    }
  };

  const goToNext = () => {
    if (currentSlide === images.length - 1) {
      setCurrentSlide(0);
    } else {
      setCurrentSlide(currentSlide + 1);
    }
  };

  // Auto-advance slideshow
  useEffect(() => {
    let interval = null;
    if (isSlideshow && images.length > 0) {
      interval = setInterval(() => {
        goToNext();
      }, 4000); // Change image every 4 seconds
    }
    return () => clearInterval(interval);
  }, [isSlideshow, images.length, currentSlide]);

  // Keyboard navigation
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (selectedImageIndex === null) return;

      if (e.key === 'Escape') {
        closeLightbox();
        setIsSlideshow(false);
      } else if (e.key === 'ArrowLeft') {
        goToPrevious();
      } else if (e.key === 'ArrowRight') {
        goToNext();
      } else if (e.key === ' ') { // Spacebar to toggle slideshow
        e.preventDefault();
        setIsSlideshow(prev => !prev);
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [selectedImageIndex, currentSlide, images.length]);

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-gray-700 text-xl">Loading gallery...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-red-500 text-xl">{error}</div>
      </div>
    );
  }

  return (
    <>
      {images.length > 0 ? (
        <div className="gallery-header">
          <h1>Photography</h1>
          <div className="image-count">
            {images.length} {images.length === 1 ? 'photograph' : 'photographs'}
          </div>
        </div>
      ) : (
        <div className="empty-gallery">
          <div className="text-5xl mb-4">ðŸ“·</div>
          <h2 className="text-2xl font-light mb-2" style={{color: '#333333'}}>No Photos Yet</h2>
          <p style={{color: '#333333'}}>Check back later for new photographs</p>
        </div>
      )}

      {images.length > 0 && (
        <div className="masonry-grid">
          {images.map((image, index) => (
            <div 
              key={image.id} 
              className="image-container group cursor-pointer" 
              style={{ aspectRatio: image.width / image.height }}
              onClick={() => openLightbox(index)}
            >
              <img
                src={`http://localhost:5000${image.path}`}
                sizes="(max-width: 640px) 100vw, (max-width: 768px) 50vw, (max-width: 1024px) 33vw, 25vw"
                alt={image.original_name}
                className="w-full h-full object-cover"
                loading="lazy"
              />
            </div>
          ))}
        </div>
      )}

      {/* Lightbox */}
      {selectedImageIndex !== null && images.length > 0 && (
        <div 
          className="lightbox-overlay"
          onClick={closeLightbox}
        >
          <div className="lightbox-content">
            <img
              src={`http://localhost:5000${images[currentSlide].path}`}
              alt={images[currentSlide].original_name}
              className="lightbox-image"
              onClick={(e) => e.stopPropagation()}
            />

            <button
              className="close-button"
              onClick={() => {
                closeLightbox();
                setIsSlideshow(false);
              }}
            >
              <FiX />
            </button>

            <div className="lightbox-nav">
              <button className="nav-button" onClick={(e) => { e.stopPropagation(); goToPrevious(); }}>
                <FiChevronLeft />
              </button>
              <button className="nav-button" onClick={(e) => { e.stopPropagation(); goToNext(); }}>
                <FiChevronRight />
              </button>
            </div>

            <div className="absolute top-4 left-0 right-0 text-center text-gray-700 text-sm" style={{color: '#333333'}}>
              {currentSlide + 1} of {images.length}
              <button 
                className="ml-4 text-xs underline"
                onClick={(e) => { 
                  e.stopPropagation(); 
                  setIsSlideshow(!isSlideshow); 
                }}
                style={{color: '#007BFF'}}
              >
                {isSlideshow ? 'Pause' : 'Play'} Slideshow
              </button>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default GalleryPage;
