import React, { useCallback, useState } from 'react';
import { useDropzone } from 'react-dropzone';
import axios from 'axios';
import { FiUpload, FiX, FiImage, FiCheck, FiAlertTriangle, FiFile, FiTrash2 } from 'react-icons/fi';

const UploadPage = () => {
  const [files, setFiles] = useState([]);
  const [uploading, setUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [uploadSuccess, setUploadSuccess] = useState(false);
  const [error, setError] = useState('');
  const [uploadStats, setUploadStats] = useState({ total: 0, successful: 0, failed: 0 });

  const onDrop = useCallback((acceptedFiles) => {
    // Filter out non-image files
    const imageFiles = acceptedFiles.filter(file => file.type.startsWith('image/'));

    // Validate file size (max 10MB)
    const validFiles = imageFiles.filter(file => file.size <= 10 * 1024 * 1024);

    // Show error for files that are too large
    const invalidFiles = imageFiles.filter(file => file.size > 10 * 1024 * 1024);
    if (invalidFiles.length > 0) {
      setError(`${invalidFiles.length} file(s) are too large (max 10MB)`);
      setTimeout(() => setError(''), 3000);
    }

    // Add valid files with preview URLs
    const newFiles = validFiles.map(file => {
      file.preview = URL.createObjectURL(file);
      return file;
    });

    setFiles(prev => [...prev, ...newFiles]);
  }, []);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'image/*': ['.jpeg', '.jpg', '.png', '.gif', '.webp']
    },
    maxFiles: 10,
    multiple: true
  });

  const removeFile = (index) => {
    const newFiles = [...files];
    // Revoke the preview URL to free up memory
    URL.revokeObjectURL(newFiles[index].preview);
    newFiles.splice(index, 1);
    setFiles(newFiles);
  };

  const uploadFiles = async () => {
    if (files.length === 0) {
      setError('Please select at least one image to upload');
      return;
    }

    setUploading(true);
    setUploadProgress(0);
    setError('');
    setUploadStats({ total: files.length, successful: 0, failed: 0 });

    const formData = new FormData();
    files.forEach(file => {
      formData.append('images', file);
    });

    try {
      const response = await axios.post('/images/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        },
        onUploadProgress: (progressEvent) => {
          const progress = Math.round(
            (progressEvent.loaded * 100) / progressEvent.total
          );
          setUploadProgress(progress);
        }
      });

      if (response.status === 201) {
        setUploadSuccess(true);
        setUploadStats(prev => ({ ...prev, successful: response.data.images.length }));
        setFiles([]);
        setTimeout(() => {
          setUploadSuccess(false);
        }, 3000);
      }
    } catch (err) {
      setError(err.response?.data?.message || 'Upload failed');
      setUploadStats(prev => ({ ...prev, failed: prev.total }));
    } finally {
      setUploading(false);
    }
  };

  const clearAll = () => {
    // Revoke all preview URLs
    files.forEach(file => URL.revokeObjectURL(file.preview));
    setFiles([]);
  };

  // Calculate total file size
  const totalSize = files.reduce((sum, file) => sum + file.size, 0);
  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  return (
    <>
      <div className="gallery-header">
        <h1 className="text-3xl">Upload Photos</h1>
        <p className="text-[#001F3F]/80">Share your visual stories with the world</p>
      </div>

      <div className="px-4 md:px-8">
        <div className="max-w-6xl mx-auto">
          {error && (
            <div className="bg-red-100 text-red-700 p-4 rounded-xl mb-6 border border-red-200 flex items-center animate-pulse">
              <FiAlertTriangle className="mr-2 flex-shrink-0" /> {error}
            </div>
          )}

          {uploadSuccess && (
            <div className="bg-green-100 text-green-700 p-4 rounded-xl mb-6 flex items-center border border-green-200">
              <FiCheck className="mr-2 text-xl flex-shrink-0" /> 
              <div>
                <p className="font-medium">Upload successful!</p>
                <p className="text-sm">{uploadStats.successful} images uploaded</p>
              </div>
            </div>
          )}

          {/* Dropzone Area */}
          <div
            {...getRootProps()}
            className={`border-2 border-dashed rounded-2xl p-12 text-center cursor-pointer transition-all duration-300 ${
              isDragActive 
                ? 'border-[#A8E6CF] bg-[#FFF5E1]/80 scale-105' 
                : 'border-[#001F3F] hover:border-[#A8E6CF] bg-[#FFF5E1]/60 hover:bg-[#FFF5E1]/80'
            } shadow-md hover:shadow-lg`}
          >
            <input {...getInputProps()} />
            <div className="flex flex-col items-center justify-center">
              <div className="w-16 h-16 rounded-full bg-gradient-to-br from-[#A8E6CF] to-[#FF6F61] flex items-center justify-center mb-5">
                <FiUpload className="text-white text-3xl" />
              </div>
              <h3 className="text-xl font-medium text-[#001F3F] mb-2">
                {isDragActive ? 'Drop your images here' : 'Drag & drop your images'}
              </h3>
              <p className="text-[#001F3F]/80 mb-2">
                {isDragActive ? 'Perfect, release the mouse!' : 'or click to browse files'}
              </p>
              <p className="text-[#001F3F]/60 text-sm">
                Supports: JPEG, PNG, GIF, WEBP (Max 10MB each)
              </p>
            </div>
          </div>

          {/* Upload Progress */}
          {uploading && (
            <div className="mt-8 bg-white/50 backdrop-blur-sm rounded-2xl p-6 shadow-md">
              <div className="flex justify-between items-center mb-3">
                <span className="text-[#001F3F] font-medium">Uploading images...</span>
                <span className="text-[#001F3F] font-medium">{uploadProgress}%</span>
              </div>
              <div className="w-full bg-[#708090]/30 rounded-full h-3 overflow-hidden">
                <div
                  className="bg-gradient-to-r from-[#FF6F61] to-[#A8E6CF] h-3 rounded-full transition-all duration-300 ease-out"
                  style={{ width: `${uploadProgress}%` }}
                ></div>
              </div>
              <div className="mt-3 text-sm text-[#001F3F]/70">
                <p>Processed: {Math.round(uploadProgress * files.length / 100)}/{files.length} images</p>
              </div>
            </div>
          )}

          {/* Selected Files Preview */}
          {files.length > 0 && (
            <div className="mt-10">
              <div className="flex flex-wrap justify-between items-center mb-6 gap-4">
                <div>
                  <h2 className="text-xl font-medium text-[#001F3F]">
                    Selected Images ({files.length})
                  </h2>
                  <p className="text-[#001F3F]/70 text-sm">
                    Total size: {formatFileSize(totalSize)}
                  </p>
                </div>
                <div className="flex gap-3">
                  <button
                    onClick={clearAll}
                    disabled={uploading}
                    className="px-4 py-2 bg-white/70 hover:bg-white border border-[#708090]/50 rounded-xl text-[#001F3F] hover:text-[#FF6F61] transition-colors flex items-center disabled:opacity-50"
                  >
                    <FiTrash2 className="mr-2" /> Clear All
                  </button>
                </div>
              </div>

              <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 xl:grid-cols-6 gap-4">
                {files.map((file, index) => (
                  <div 
                    key={index} 
                    className="relative group bg-white/50 rounded-xl overflow-hidden shadow-sm hover:shadow-md transition-shadow"
                  >
                    <div className="aspect-square bg-[#FFF5E1] rounded-lg overflow-hidden">
                      <img
                        src={file.preview}
                        alt={file.name}
                        className="w-full h-full object-cover"
                      />
                    </div>

                    <div className="absolute inset-0 bg-black/50 opacity-0 group-hover:opacity-100 transition-opacity flex items-center justify-center">
                      <button
                        onClick={(e) => {
                          e.stopPropagation();
                          removeFile(index);
                        }}
                        className="bg-white text-[#001F3F] rounded-full p-2 shadow-lg hover:bg-[#FF6F61] hover:text-white transition-colors"
                        aria-label="Remove image"
                      >
                        <FiX size={18} />
                      </button>
                    </div>

                    <div className="absolute bottom-0 left-0 right-0 bg-gradient-to-t from-black/80 to-transparent p-3 text-white text-xs truncate">
                      <p className="font-medium truncate">{file.name}</p>
                      <p className="text-[#A8E6CF]">{formatFileSize(file.size)}</p>
                    </div>

                    <div className="absolute top-2 right-2 bg-white/80 rounded-full p-1 text-[#001F3F]">
                      <FiImage size={14} />
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}

          {/* Upload Button */}
          <div className="mt-10 flex flex-col items-center">
            <button
              onClick={uploadFiles}
              disabled={uploading || files.length === 0}
              className={`px-8 py-4 rounded-2xl font-medium flex items-center text-lg transition-all duration-300 transform hover:scale-105 ${
                uploading || files.length === 0
                  ? 'bg-gray-300 cursor-not-allowed text-gray-500'
                  : 'bg-gradient-to-r from-[#FF6F61] to-[#A8E6CF] text-white shadow-lg hover:shadow-xl'
              }`}
            >
              {uploading ? (
                <>
                  <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                    <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                    <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                  </svg>
                  Uploading {files.length} images...
                </>
              ) : (
                <>
                  <FiUpload className="mr-3" />
                  Upload {files.length} Image{files.length !== 1 ? 's' : ''}
                </>
              )}
            </button>

            <p className="text-[#001F3F]/60 mt-4 text-sm">
              {files.length > 0 
                ? `Total: ${formatFileSize(totalSize)} • ${files.length} image${files.length !== 1 ? 's' : ''}`
                : 'Select images to see total size'}
            </p>
          </div>
        </div>
      </div>
    </>
  );
};

export default UploadPage;
