import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const AdminPage = () => {
  const [user, setUser] = useState(null);
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [dashboardData, setDashboardData] = useState({
    totalPhotos: 0,
    storageUsed: '0 MB',
    lastUpload: 'Never'
  });

  useEffect(() => {
    // Get user info and images
    const fetchData = async () => {
      try {
        const userResponse = await axios.get('/auth/me');
        setUser(userResponse.data);

        const imagesResponse = await axios.get('/images/my-images');
        setImages(imagesResponse.data);

        // Calculate dashboard stats
        calculateDashboardStats(imagesResponse.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const calculateDashboardStats = (images) => {
    // Calculate total photos
    const totalPhotos = images.length;

    // Calculate storage used (approximate)
    const totalSize = images.reduce((sum, image) => sum + image.size, 0);
    const storageUsed = formatBytes(totalSize);

    // Calculate last upload
    let lastUpload = 'Never';
    if (images.length > 0) {
      const latestImage = images.reduce((latest, image) => 
        new Date(image.createdAt) > new Date(latest.createdAt) ? image : latest, images[0]
      );
      lastUpload = new Date(latestImage.createdAt).toLocaleDateString();
    }

    setDashboardData({
      totalPhotos,
      storageUsed,
      lastUpload
    });
  };

  const formatBytes = (bytes, decimals = 2) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const dm = decimals < 0 ? 0 : decimals;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(dm)) + ' ' + sizes[i];
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    delete axios.defaults.headers.common['Authorization'];
    window.location.href = '/login';
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-dark-900">
        <div className="text-white text-xl">Loading...</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-dark-900">
      {/* Admin Header */}
      <header className="bg-dark-800 shadow-lg">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-4">
            <div>
              <h1 className="text-2xl font-bold text-white">Photographer Admin</h1>
              <p className="text-dark-300">Manage your portfolio</p>
            </div>
            <div className="flex items-center space-x-4">
              <span className="text-dark-300">Welcome, {user?.username}</span>
              <Link
                to="/"
                className="bg-primary-600 hover:bg-primary-700 text-white px-4 py-2 rounded-lg font-medium"
              >
                View Gallery
              </Link>
              <button
                onClick={handleLogout}
                className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded-lg font-medium"
              >
                Logout
              </button>
            </div>
          </div>
        </div>
      </header>

      {/* Admin Navigation */}
      <nav className="bg-dark-800 border-b border-dark-700">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex space-x-8">
            <Link
              to="/admin"
              className="py-4 px-1 border-b-2 border-primary-500 text-white font-medium"
            >
              Dashboard
            </Link>
            <Link
              to="/admin/my-gallery"
              className="py-4 px-1 border-b-2 border-transparent text-dark-300 hover:text-white hover:border-dark-300 font-medium"
            >
              My Gallery
            </Link>
            <Link
              to="/admin/upload"
              className="py-4 px-1 border-b-2 border-transparent text-dark-300 hover:text-white hover:border-dark-300 font-medium"
            >
              Upload Photos
            </Link>
          </div>
        </div>
      </nav>

      {/* Admin Content */}
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="mb-8">
          <h2 className="text-2xl font-bold text-white mb-2">Admin Dashboard</h2>
          <p className="text-dark-300">
            Welcome to your admin panel. Manage your photos and portfolio settings here.
          </p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
          <div className="bg-dark-800 rounded-lg p-6 shadow">
            <h3 className="text-lg font-semibold text-white mb-2">Total Photos</h3>
            <p className="text-3xl font-bold text-primary-400">{dashboardData.totalPhotos}</p>
          </div>
          <div className="bg-dark-800 rounded-lg p-6 shadow">
            <h3 className="text-lg font-semibold text-white mb-2">Storage Used</h3>
            <p className="text-3xl font-bold text-primary-400">{dashboardData.storageUsed}</p>
          </div>
          <div className="bg-dark-800 rounded-lg p-6 shadow">
            <h3 className="text-lg font-semibold text-white mb-2">Last Upload</h3>
            <p className="text-3xl font-bold text-primary-400">{dashboardData.lastUpload}</p>
          </div>
        </div>

        <div className="bg-dark-800 rounded-lg p-6 shadow">
          <h3 className="text-lg font-semibold text-white mb-4">Quick Actions</h3>
          <div className="flex flex-wrap gap-4">
            <Link
              to="/admin/upload"
              className="bg-primary-600 hover:bg-primary-700 text-white px-6 py-3 rounded-lg font-medium inline-flex items-center"
            >
              <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 16v1a3 3 0 003 3h10a3 3 0 003-3v-1m-4-8l-4-4m0 0L8 8m4-4v12"></path>
              </svg>
              Upload Photos
            </Link>
            <Link
              to="/admin/my-gallery"
              className="bg-dark-600 hover:bg-dark-500 text-white px-6 py-3 rounded-lg font-medium inline-flex items-center"
            >
              <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 6a2 2 0 012-2h2a2 2 0 012 2v2a2 2 0 01-2 2H6a2 2 0 01-2-2V6zM14 6a2 2 0 012-2h2a2 2 0 012 2v2a2 2 0 01-2 2h-2a2 2 0 01-2-2V6zM4 16a2 2 0 012-2h2a2 2 0 012 2v2a2 2 0 01-2 2H6a2 2 0 01-2-2v-2zM14 16a2 2 0 012-2h2a2 2 0 012 2v2a2 2 0 01-2 2h-2a2 2 0 01-2-2v-2z"></path>
              </svg>
              My Gallery
            </Link>
            <Link
              to="/"
              className="bg-dark-600 hover:bg-dark-500 text-white px-6 py-3 rounded-lg font-medium inline-flex items-center"
            >
              <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M15 12a3 3 0 11-6 0 3 3 0 016 0z"></path>
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z"></path>
              </svg>
              View Public Gallery
