import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import axios from 'axios';
import UploadPage from './pages/UploadPage';
import GalleryPage from './pages/GalleryPage';
import Login from './pages/Login';

// Set base URL for API
axios.defaults.baseURL = 'http://localhost:5000/api';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Check if user is authenticated on app load
    const token = localStorage.getItem('token');
    if (token) {
      // Verify token with backend
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
      axios.get('/auth/me')
        .then(() => setIsAuthenticated(true))
        .catch(() => {
          localStorage.removeItem('token');
          delete axios.defaults.headers.common['Authorization'];
        })
        .finally(() => setLoading(false));
    } else {
      setLoading(false);
    }
  }, []);

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-dark-900">
        <div className="text-white text-xl">Loading...</div>
      </div>
    );
  }

  return (
    <Router>
      <Routes>
        <Route 
          path="/login" 
          element={!isAuthenticated ? <Login setIsAuthenticated={setIsAuthenticated} /> : <Navigate to="/gallery" />} 
        />
        <Route 
          path="/upload" 
          element={isAuthenticated ? <UploadPage /> : <Navigate to="/login" />} 
        />
        <Route 
          path="/gallery" 
          element={isAuthenticated ? <GalleryPage /> : <Navigate to="/login" />} 
        />
        <Route 
          path="/" 
          element={<Navigate to="/gallery" />} 
        />
      </Routes>
    </Router>
  );
}

export default App;
