import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const AdminPage = () => {
  const [user, setUser] = useState(null);
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [dashboardData, setDashboardData] = useState({
    totalPhotos: 0,
    storageUsed: '0 MB',
    lastUpload: 'Never'
  });

  useEffect(() => {
    // Get user info and images
    const fetchData = async () => {
      try {
        const userResponse = await axios.get('/auth/me');
        setUser(userResponse.data);

        const imagesResponse = await axios.get('/images/my-images');
        setImages(imagesResponse.data);

        // Calculate dashboard stats
        calculateDashboardStats(imagesResponse.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const calculateDashboardStats = (images) => {
    // Calculate total photos
    const totalPhotos = images.length;

    // Calculate storage used (approximate)
    const totalSize = images.reduce((sum, image) => sum + image.size, 0);
    const storageUsed = formatBytes(totalSize);

    // Calculate last upload
    let lastUpload = 'Never';
    if (images.length > 0) {
      const latestImage = images.reduce((latest, image) => 
        new Date(image.createdAt) > new Date(latest.createdAt) ? image : latest, images[0]
      );
      lastUpload = new Date(latestImage.createdAt).toLocaleDateString();
    }

    setDashboardData({
      totalPhotos,
      storageUsed,
      lastUpload
    });
  };

  const formatBytes = (bytes, decimals = 2) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const dm = decimals < 0 ? 0 : decimals;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(dm)) + ' ' + sizes[i];
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    delete axios.defaults.headers.common['Authorization'];
    window.location.href = '/login';
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-gray-700 text-xl">Loading...</div>
      </div>
    );
  }

  return (
    <>
      <div className="gallery-header">
        <h1>Admin Dashboard</h1>
        <div className="flex items-center gap-4 mt-2">
          <span style={{color: '#333333'}}>Welcome, {user?.username}</span>
          <Link
            to="/"
            style={{color: '#007BFF'}}
            className="text-sm"
          >
            View Gallery
          </Link>
          <button
            onClick={handleLogout}
            style={{color: '#007BFF'}}
            className="text-sm"
          >
            Logout
          </button>
        </div>
      </div>

      <div className="px-4 md:px-8">
        <div className="max-w-6xl mx-auto py-8">
          <div className="mb-8">
            <h2 className="text-xl font-medium" style={{color: '#333333'}}>Admin Dashboard</h2>
            <p style={{color: '#333333'}}>
              Welcome to your admin panel. Manage your photos and portfolio settings here.
            </p>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
            <div className="bg-white rounded-lg p-6" style={{border: '1px solid #333333'}}>
              <h3 className="text-sm font-medium" style={{color: '#333333'}}>Total Photos</h3>
              <p className="text-2xl font-semibold" style={{color: '#333333'}}>{dashboardData.totalPhotos}</p>
            </div>
            <div className="bg-white rounded-lg p-6" style={{border: '1px solid #333333'}}>
              <h3 className="text-sm font-medium" style={{color: '#333333'}}>Storage Used</h3>
              <p className="text-2xl font-semibold" style={{color: '#333333'}}>{dashboardData.storageUsed}</p>
            </div>
            <div className="bg-white rounded-lg p-6" style={{border: '1px solid #333333'}}>
              <h3 className="text-sm font-medium" style={{color: '#333333'}}>Last Upload</h3>
              <p className="text-2xl font-semibold" style={{color: '#333333'}}>{dashboardData.lastUpload}</p>
            </div>
          </div>

          <div className="bg-white rounded-lg p-6" style={{border: '1px solid #333333'}}>
            <h3 className="text-lg font-medium" style={{color: '#333333'}}>Quick Actions</h3>
            <div className="flex flex-wrap gap-3">
              <Link
                to="/admin/upload"
                style={{backgroundColor: '#007BFF', color: 'white'}}
                className="px-5 py-2.5 rounded font-medium inline-flex items-center text-sm"
              >
                <svg className="w-4 h-4 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 16v1a3 3 0 003 3h10a3 3 0 003-3v-1m-4-8l-4-4m0 0L8 8m4-4v12"></path>
                </svg>
                Upload Photos
              </Link>
              <Link
                to="/admin/my-gallery"
                style={{color: '#333333', border: '1px solid #333333', backgroundColor: 'white'}}
                className="px-5 py-2.5 rounded font-medium inline-flex items-center text-sm"
              >
                <svg className="w-4 h-4 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 6a2 2 0 012-2h2a2 2 0 012 2v2a2 2 0 01-2 2H6a2 2 0 01-2-2V6zM14 6a2 2 0 012-2h2a2 2 0 012 2v2a2 2 0 01-2 2h-2a2 2 0 01-2-2V6zM4 16a2 2 0 012-2h2a2 2 0 012 2v2a2 2 0 01-2 2H6a2 2 0 01-2-2v-2zM14 16a2 2 0 012-2h2a2 2 0 012 2v2a2 2 0 01-2 2h-2a2 2 0 01-2-2v-2z"></path>
                </svg>
                My Gallery
              </Link>
              <Link
                to="/"
                style={{color: '#333333', border: '1px solid #333333', backgroundColor: 'white'}}
                className="px-5 py-2.5 rounded font-medium inline-flex items-center text-sm"
              >
                <svg className="w-4 h-4 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M15 12a3 3 0 11-6 0 3 3 0 016 0z"></path>
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z"></path>
                </svg>
                View Public Gallery
              </Link>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default AdminPage;
