import React, { useCallback, useState } from 'react';
import { useDropzone } from 'react-dropzone';
import axios from 'axios';
import { FiUpload, FiX, FiImage, FiCheck } from 'react-icons/fi';

const UploadPage = () => {
  const [files, setFiles] = useState([]);
  const [uploading, setUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [uploadSuccess, setUploadSuccess] = useState(false);
  const [error, setError] = useState('');

  const onDrop = useCallback((acceptedFiles) => {
    // Filter out non-image files
    const imageFiles = acceptedFiles.filter(file => file.type.startsWith('image/'));

    // Validate file size (max 10MB)
    const validFiles = imageFiles.filter(file => file.size <= 10 * 1024 * 1024);

    // Show error for files that are too large
    const invalidFiles = imageFiles.filter(file => file.size > 10 * 1024 * 1024);
    if (invalidFiles.length > 0) {
      setError(`${invalidFiles.length} file(s) are too large (max 10MB)`);
      setTimeout(() => setError(''), 3000);
    }

    // Add valid files with preview URLs
    const newFiles = validFiles.map(file => {
      file.preview = URL.createObjectURL(file);
      return file;
    });

    setFiles(prev => [...prev, ...newFiles]);
  }, []);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'image/*': ['.jpeg', '.jpg', '.png', '.gif', '.webp']
    },
    maxFiles: 10,
    multiple: true
  });

  const removeFile = (index) => {
    const newFiles = [...files];
    // Revoke the preview URL to free up memory
    URL.revokeObjectURL(newFiles[index].preview);
    newFiles.splice(index, 1);
    setFiles(newFiles);
  };

  const uploadFiles = async () => {
    if (files.length === 0) {
      setError('Please select at least one image to upload');
      return;
    }

    setUploading(true);
    setUploadProgress(0);
    setError('');

    const formData = new FormData();
    files.forEach(file => {
      formData.append('images', file);
    });

    try {
      const response = await axios.post('/images/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        },
        onUploadProgress: (progressEvent) => {
          const progress = Math.round(
            (progressEvent.loaded * 100) / progressEvent.total
          );
          setUploadProgress(progress);
        }
      });

      if (response.status === 201) {
        setUploadSuccess(true);
        setFiles([]);
        setTimeout(() => {
          setUploadSuccess(false);
        }, 3000);
      }
    } catch (err) {
      setError(err.response?.data?.message || 'Upload failed');
    } finally {
      setUploading(false);
    }
  };

  const clearAll = () => {
    // Revoke all preview URLs
    files.forEach(file => URL.revokeObjectURL(file.preview));
    setFiles([]);
  };

  return (
    <>
      <div className="gallery-header">
        <h1>Upload Photos</h1>
      </div>

      <div className="px-4 md:px-8">
        <div className="max-w-6xl mx-auto">
          {error && (
            <div className="bg-red-100 text-red-700 p-3 rounded-lg mb-4 border border-red-200">
              {error}
            </div>
          )}

          {uploadSuccess && (
            <div className="bg-green-100 text-green-700 p-3 rounded-lg mb-4 flex items-center border border-green-200">
              <FiCheck className="mr-2" /> Upload successful!
            </div>
          )}

          {/* Dropzone Area */}
          <div
            {...getRootProps()}
            className="border-2 border-dashed rounded-lg p-12 text-center cursor-pointer transition-colors"
            style={{
              borderColor: isDragActive ? '#007BFF' : '#333333',
              backgroundColor: '#F5F5F5',
              borderStyle: 'dashed'
            }}
          >
            <input {...getInputProps()} />
            <div className="flex flex-col items-center justify-center">
              <FiUpload style={{color: '#333333', fontSize: '2rem'}} className="mb-3" />
              <p style={{color: '#333333', marginBottom: '0.25rem'}}>
                {isDragActive ? 'Drop the images here!' : 'Drag & drop images here'}
              </p>
              <p style={{color: '#333333', fontSize: '0.875rem'}}>
                or click to browse
              </p>
              <p style={{color: '#333333', fontSize: '0.875rem', marginTop: '0.5rem'}}>
                Supports: JPEG, PNG, GIF, WEBP (Max 10MB each)
              </p>
            </div>
          </div>

          {/* Upload Progress */}
          {uploading && (
            <div className="mt-6 rounded-lg p-4" style={{backgroundColor: '#F5F5F5'}}>
              <div className="flex justify-between items-center mb-2">
                <span style={{color: '#333333'}}>Uploading...</span>
                <span style={{color: '#333333'}}>{uploadProgress}%</span>
              </div>
              <div className="w-full rounded-full h-2" style={{backgroundColor: '#333333'}}>
                <div
                  style={{ width: `${uploadProgress}%`, backgroundColor: '#007BFF', height: '100%', borderRadius: '9999px', transition: 'all 300ms ease'}}
                ></div>
              </div>
            </div>
          )}

          {/* Selected Files Preview */}
          {files.length > 0 && (
            <div className="mt-8">
              <div className="flex justify-between items-center mb-4">
                <h2 className="text-lg font-medium" style={{color: '#333333'}}>
                  Selected Images ({files.length})
                </h2>
                {files.length > 0 && (
                  <button
                    onClick={clearAll}
                    style={{color: '#007BFF'}}
                    className="hover:text-[#0069d9] text-sm"
                  >
                    Clear All
                  </button>
                )}
              </div>
              <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-6 gap-3">
                {files.map((file, index) => (
                  <div key={index} className="relative group">
                    <div className="aspect-square rounded overflow-hidden" style={{backgroundColor: '#F5F5F5'}}>
                      <img
                        src={file.preview}
                        alt={file.name}
                        className="w-full h-full object-cover"
                      />
                    </div>
                    <div className="absolute inset-0 bg-black/50 opacity-0 group-hover:opacity-100 transition-opacity flex items-center justify-center">
                      <button
                        onClick={(e) => {
                          e.stopPropagation();
                          removeFile(index);
                        }}
                        style={{color: '#333333'}}
                        className="bg-white rounded-full p-1"
                      >
                        <FiX size={16} />
                      </button>
                    </div>
                    <div className="absolute bottom-0 left-0 right-0 bg-white/80 p-1 text-xs truncate" style={{color: '#333333'}}>
                      {file.name}
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}

          {/* Upload Button */}
          <div className="mt-8 flex justify-center">
            <button
              onClick={uploadFiles}
              disabled={uploading || files.length === 0}
              className="px-6 py-3 rounded font-medium flex items-center"
              style={{
                backgroundColor: (uploading || files.length === 0) ? '#F5F5F5' : '#007BFF',
                color: (uploading || files.length === 0) ? '#333333' : 'white',
                cursor: (uploading || files.length === 0) ? 'not-allowed' : 'pointer'
              }}
            >
              <FiUpload className="mr-2" />
              {uploading ? 'Uploading...' : `Upload ${files.length} Image(s)`}
            </button>
          </div>
        </div>
      </div>
    </>
  );
};

export default UploadPage;
