import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FiX, FiChevronLeft, FiChevronRight, FiHeart } from 'react-icons/fi';

const GalleryPage = () => {
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [selectedImageIndex, setSelectedImageIndex] = useState(null);
  const [currentSlide, setCurrentSlide] = useState(0);
  const [isSlideshow, setIsSlideshow] = useState(false);
  const [favorites, setFavorites] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [showCategories, setShowCategories] = useState(false);

  // Load favorites from localStorage on initial render
  useEffect(() => {
    const savedFavorites = localStorage.getItem('favorites');
    if (savedFavorites) {
      setFavorites(JSON.parse(savedFavorites));
    }
  }, []);

  // Save favorites to localStorage whenever favorites change
  useEffect(() => {
    localStorage.setItem('favorites', JSON.stringify(favorites));
  }, [favorites]);

  useEffect(() => {
    fetchImages();
  }, []);

  const fetchImages = async () => {
    try {
      const response = await axios.get('/images');
      setImages(response.data);
      setLoading(false);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to load images');
      setLoading(false);
    }
  };

  const openLightbox = (index) => {
    setSelectedImageIndex(index);
    setCurrentSlide(index);
  };

  const closeLightbox = () => {
    setSelectedImageIndex(null);
  };

  const goToPrevious = () => {
    if (currentSlide === 0) {
      setCurrentSlide(images.length - 1);
    } else {
      setCurrentSlide(currentSlide - 1);
    }
  };

  const goToNext = () => {
    if (currentSlide === images.length - 1) {
      setCurrentSlide(0);
    } else {
      setCurrentSlide(currentSlide + 1);
    }
  };

  // Auto-advance slideshow
  useEffect(() => {
    let interval = null;
    if (isSlideshow && images.length > 0) {
      interval = setInterval(() => {
        goToNext();
      }, 4000); // Change image every 4 seconds
    }
    return () => clearInterval(interval);
  }, [isSlideshow, images.length, currentSlide]);

  // Keyboard navigation
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (selectedImageIndex === null) return;

      if (e.key === 'Escape') {
        closeLightbox();
        setIsSlideshow(false);
      } else if (e.key === 'ArrowLeft') {
        goToPrevious();
      } else if (e.key === 'ArrowRight') {
        goToNext();
      } else if (e.key === ' ') { // Spacebar to toggle slideshow
        e.preventDefault();
        setIsSlideshow(prev => !prev);
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [selectedImageIndex, currentSlide, images.length]);

  // Toggle favorite status for an image
  const toggleFavorite = (imageId) => {
    if (favorites.includes(imageId)) {
      setFavorites(favorites.filter(id => id !== imageId));
    } else {
      setFavorites([...favorites, imageId]);
    }
  };

  // Filter images by category
  const filteredImages = selectedCategory === 'all' 
    ? images 
    : images.filter(image => image.category === selectedCategory);

  // Extract unique categories
  const categories = ['all', ...new Set(images.map(img => img.category || 'uncategorized'))];

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="flex flex-col items-center">
          <div className="w-12 h-12 border-4 border-[#FF6F61] border-t-transparent rounded-full animate-spin mb-4"></div>
          <div className="text-cream-900 text-xl">Loading gallery...</div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-red-500 text-xl">{error}</div>
      </div>
    );
  }

  return (
    <>
      <div className="gallery-header">
        <h1>Photography</h1>
        <div className="image-count">
          {images.length} {images.length === 1 ? 'photograph' : 'photographs'}
        </div>

        {/* Category filter dropdown */}
        <div className="relative mt-4 inline-block">
          <button 
            className="bg-[#A8E6CF] text-[#001F3F] px-4 py-2 rounded-md font-medium flex items-center"
            onClick={() => setShowCategories(!showCategories)}
            onMouseEnter={() => setShowCategories(true)} // Show on hover
          >
            {selectedCategory === 'all' ? 'All Categories' : selectedCategory}
            <svg className="w-4 h-4 ml-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 9l-7 7-7-7"></path>
            </svg>
          </button>

          {showCategories && (
            <div 
              className="absolute left-0 mt-1 w-full bg-[#A8E6CF] border border-[#708090] rounded-md shadow-lg z-10"
              onMouseLeave={() => setShowCategories(false)} // Hide when not hovering
            >
              {categories.map((category, index) => (
                <button
                  key={index}
                  className={`block w-full text-left px-4 py-2 hover:bg-[#7fc9ae] ${selectedCategory === category ? 'bg-[#7fc9ae]' : ''}`}
                  onClick={() => {
                    setSelectedCategory(category);
                    setShowCategories(false);
                  }}
                >
                  {category === 'all' ? 'All Categories' : category}
                </button>
              ))}
            </div>
          )}
        </div>
      </div>

      {filteredImages.length > 0 ? (
        <div className="masonry-grid">
          {filteredImages.map((image, index) => (
            <div 
              key={image.id} 
              className="image-container group cursor-pointer relative" 
              style={{ aspectRatio: image.width / image.height }}
              onClick={() => openLightbox(images.findIndex(img => img.id === image.id))}
            >
              <img
                src={`http://localhost:5000${image.path}`}
                sizes="(max-width: 640px) 100vw, (max-width: 768px) 50vw, (max-width: 1024px) 33vw, 25vw"
                alt={image.original_name}
                className="w-full h-full object-cover border border-[#708090]"
                loading="lazy"
              />

              {/* Favorite button */}
              <button
                className={`absolute top-2 right-2 p-1.5 rounded-full transition-colors ${
                  favorites.includes(image.id) 
                    ? 'bg-[#FF6F61] text-white' 
                    : 'bg-white/70 text-[#001F3F]'
                }`}
                onClick={(e) => {
                  e.stopPropagation();
                  toggleFavorite(image.id);
                }}
              >
                <FiHeart 
                  className={favorites.includes(image.id) ? 'fill-current' : ''} 
                  size={16} 
                />
              </button>
            </div>
          ))}
        </div>
      ) : (
        <div className="empty-gallery">
          <div className="text-5xl mb-4">ðŸ“·</div>
          <h2 className="text-2xl font-light mb-2 text-[#001F3F]">
            No Photos in this Category
          </h2>
          <p className="text-center text-[#001F3F]">
            Try selecting a different category
          </p>
        </div>
      )}

      {/* Lightbox */}
      {selectedImageIndex !== null && images.length > 0 && (
        <div 
          className="lightbox-overlay"
          onClick={closeLightbox}
        >
          <div className="lightbox-content">
            <img
              src={`http://localhost:5000${images[currentSlide].path}`}
              alt={images[currentSlide].original_name}
              className="lightbox-image border border-[#708090]"
              onClick={(e) => e.stopPropagation()}
            />

            <button
              className="close-button"
              onClick={() => {
                closeLightbox();
                setIsSlideshow(false);
              }}
            >
              <FiX />
            </button>

            <div className="lightbox-nav">
              <button className="nav-button" onClick={(e) => { e.stopPropagation(); goToPrevious(); }}>
                <FiChevronLeft />
              </button>
              <button className="nav-button" onClick={(e) => { e.stopPropagation(); goToNext(); }}>
                <FiChevronRight />
              </button>
            </div>

            <div className="absolute top-4 left-0 right-0 text-center text-sm text-[#001F3F]">
              {currentSlide + 1} of {images.length}
              <button 
                className="ml-4 text-xs underline text-[#A8E6CF]"
                onClick={(e) => { 
                  e.stopPropagation(); 
                  setIsSlideshow(!isSlideshow); 
                }}
              >
                {isSlideshow ? 'Pause' : 'Play'} Slideshow
              </button>

              {/* Favorite button in lightbox */}
              <button 
                className={`ml-4 text-xs ${favorites.includes(images[currentSlide].id) ? 'text-[#FF6F61]' : 'text-[#001F3F]'}`}
                onClick={(e) => {
                  e.stopPropagation();
                  toggleFavorite(images[currentSlide].id);
                }}
              >
                <FiHeart 
                  className={`inline mr-1 ${favorites.includes(images[currentSlide].id) ? 'fill-current text-[#FF6F61]' : ''}`} 
                  size={14} 
                />
                {favorites.includes(images[currentSlide].id) ? 'Favorited' : 'Favorite'}
              </button>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default GalleryPage;
