import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FiX, FiChevronLeft, FiChevronRight, FiTrash2 } from 'react-icons/fi';

const AdminGallery = () => {
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [selectedImageIndex, setSelectedImageIndex] = useState(null);
  const [deleting, setDeleting] = useState(null);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [imageToDelete, setImageToDelete] = useState(null);
  const [indexToDelete, setIndexToDelete] = useState(null);

  useEffect(() => {
    fetchImages();
  }, []);

  const fetchImages = async () => {
    try {
      const response = await axios.get('/images/my-images'); // This requires authentication
      setImages(response.data);
      setLoading(false);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to load images');
      setLoading(false);
    }
  };

  const openLightbox = (index) => {
    setSelectedImageIndex(index);
  };

  const closeLightbox = () => {
    setSelectedImageIndex(null);
  };

  const goToPrevious = () => {
    if (selectedImageIndex === 0) {
      setSelectedImageIndex(images.length - 1);
    } else {
      setSelectedImageIndex(selectedImageIndex - 1);
    }
  };

  const goToNext = () => {
    if (selectedImageIndex === images.length - 1) {
      setSelectedImageIndex(0);
    } else {
      setSelectedImageIndex(selectedImageIndex + 1);
    }
  };

  const openDeleteModal = (id, index) => {
    setImageToDelete(id);
    setIndexToDelete(index);
    setShowDeleteModal(true);
  };

  const closeDeleteModal = () => {
    setShowDeleteModal(false);
    setImageToDelete(null);
    setIndexToDelete(null);
  };

  const confirmDelete = async () => {
    if (!imageToDelete) return;

    setDeleting(imageToDelete);
    setShowDeleteModal(false);

    try {
      await axios.delete(`/images/${imageToDelete}`);

      // Update the state to remove the image
      setImages(prevImages => prevImages.filter((_, i) => i !== indexToDelete));

      // Adjust the selected index if needed
      if (selectedImageIndex !== null) {
        if (indexToDelete < selectedImageIndex) {
          setSelectedImageIndex(selectedImageIndex - 1);
        } else if (indexToDelete === selectedImageIndex) {
          setSelectedImageIndex(null);
        }
      }
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to delete image');
    } finally {
      setDeleting(null);
      setImageToDelete(null);
      setIndexToDelete(null);
    }
  };

  // Keyboard navigation
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (selectedImageIndex === null) return;

      if (e.key === 'Escape') {
        closeLightbox();
      } else if (e.key === 'ArrowLeft') {
        goToPrevious();
      } else if (e.key === 'ArrowRight') {
        goToNext();
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [selectedImageIndex, images.length]);

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-dark-900">
        <div className="text-white text-xl">Loading gallery...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-dark-900">
        <div className="text-red-500 text-xl">{error}</div>
      </div>
    );
  }

  return (
    <>
    <div className="min-h-screen bg-dark-900 p-4 md:p-8">
      <div className="max-w-7xl mx-auto">
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-3xl font-bold text-white">My Photo Gallery</h1>
          <div className="text-dark-300">
            {images.length} {images.length === 1 ? 'photo' : 'photos'}
          </div>
        </div>

        {images.length === 0 ? (
          <div className="flex flex-col items-center justify-center py-20 text-center">
            <div className="text-5xl mb-4">ðŸ“¸</div>
            <h2 className="text-2xl font-bold text-white mb-2">No Photos Yet</h2>
            <p className="text-dark-300 mb-6">Upload some beautiful photos to get started</p>
          </div>
        ) : (
          <div className="masonry-grid">
            {images.map((image, index) => (
              <div key={image.id} className="relative group cursor-pointer" 
                style={{ aspectRatio: image.width / image.height }}
                onClick={() => openLightbox(index)}>
                <img
                  src={image.path}
                  sizes="(max-width: 640px) 100vw, (max-width: 768px) 50vw, (max-width: 1024px) 33vw, 25vw"
                  alt={image.original_name}
                  className="w-full h-full object-cover rounded-lg shadow-lg transition-transform duration-300 group-hover:scale-105"
                  loading="lazy"
                />

                {/* Delete button - only show for admin gallery */}
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    openDeleteModal(image.id, index);
                  }}
                  disabled={deleting === image.id}
                  className={`absolute top-2 right-2 bg-red-500 hover:bg-red-600 text-white rounded-full p-2 opacity-0 group-hover:opacity-100 transition-opacity ${
                    deleting === image.id ? 'opacity-100' : ''
                  }`}
                >
                  {deleting === image.id ? (
                    <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
                  ) : (
                    <FiTrash2 size={16} />
                  )}
                </button>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Lightbox */}
      {selectedImageIndex !== null && images.length > 0 && (
        <div 
          className="lightbox-overlay"
          onClick={closeLightbox}
        >
          <div className="lightbox-content">
            <img
              src={images[selectedImageIndex].path}
              alt={images[selectedImageIndex].original_name}
              className="lightbox-image"
              onClick={(e) => e.stopPropagation()}
            />

            <button
              className="close-button"
              onClick={closeLightbox}
            >
              <FiX />
            </button>

            <div className="lightbox-nav">
              <button className="nav-button" onClick={(e) => { e.stopPropagation(); goToPrevious(); }}>
                <FiChevronLeft />
              </button>
              <button className="nav-button" onClick={(e) => { e.stopPropagation(); goToNext(); }}>
                <FiChevronRight />
              </button>
            </div>

            <div className="absolute bottom-4 left-0 right-0 text-center text-white text-sm">
              {selectedImageIndex + 1} of {images.length}
            </div>
          </div>
        </div>
      )}
    </div>

    {/* Custom Delete Confirmation Modal */}
    {showDeleteModal && (
      <div className="fixed inset-0 bg-black bg-opacity-75 flex items-center justify-center z-50 p-4">
        <div className="bg-dark-800 rounded-lg p-6 max-w-md w-full">
          <h3 className="text-xl font-bold text-white mb-4">Confirm Delete</h3>
          <p className="text-dark-300 mb-6">Are you sure you want to delete this image? This action cannot be undone.</p>

          <div className="flex justify-end space-x-3">
            <button
              onClick={closeDeleteModal}
              className="px-4 py-2 bg-gray-600 hover:bg-gray-700 text-white rounded-lg transition-colors"
            >
              Cancel
            </button>
            <button
              onClick={confirmDelete}
              className="px-4 py-2 bg-red-600 hover:bg-red-700 text-white rounded-lg transition-colors"
            >
              Delete
            </button>
          </div>
        </div>
      </div>
    )}
    </>
  );
};

export default AdminGallery;
